import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { BehaviorSubject, combineLatestWith, Observable } from 'rxjs';
import { PhotoQuery } from '../../../../application/ports/primary/query/photo.query';
import {
  GET_ALL_PHOTOS_QUERY_PORT,
  GetAllPhotosQueryPort,
} from '../../../../application/ports/primary/query/get-all-photos.query-port';
import { CommonModule } from '@angular/common';
import { map } from 'rxjs/operators';
import { NavbarComponent } from '@core';

@Component({
  selector: 'lib-album',
  templateUrl: './album.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, NavbarComponent],
})
export class AlbumComponent {
  private _searchSubject: BehaviorSubject<string> = new BehaviorSubject<string>(
    ''
  );

  search$ = this._searchSubject.asObservable();

  readonly photos$: Observable<PhotoQuery[]> = this._getAllPhotosQueryPort
    .getAllPhotos()
    .pipe(
      combineLatestWith(this.search$),
      map(([photos, search]: [PhotoQuery[], string]) => {
        const regexp: RegExp = new RegExp(search ?? '', 'i');
        return photos.filter((photo: PhotoQuery) => regexp.test(photo.title));
      })
    );

  constructor(
    @Inject(GET_ALL_PHOTOS_QUERY_PORT)
    private _getAllPhotosQueryPort: GetAllPhotosQueryPort
  ) {}

  searchValue(event: string): void {
    console.log(event);
    this._searchSubject.next(event);
  }
}
<lib-navbar
  [showSearch]="true"
  (searchValueEvent)="searchValue($event)"
  [summaryTitle]="'Available items'"
  [count]="(photos$ | async)?.length ?? 0"
></lib-navbar>

<div class="container mt-5">
  <div class="row">
    <ng-container *ngFor="let photo of photos$ | async">
      <div class="col-md-4 mb-4">
        <div class="card">
          <img [src]="photo.thumbnailUrl" class="card-img-top"/>
          <div class="card-body">
            <h5 class="card-title">{{ photo.title }}</h5>
            <p class="card-text">Author: {{ photo.author }}</p>
            <p class="card-text">Price: {{ photo.price }}</p>
          </div>
        </div>
      </div>
    </ng-container>
  </div>
</div>
