import { ActivatedRoute, Params, ResolveFn } from '@angular/router';
import { inject } from '@angular/core';
import {
  LOAD_USER_DETAILS_COMMAND_PORT,
  LoadUserDetailsCommandPort,
} from '../../../../application/ports/primary/command/load-user-details.command-port';
import {switchMap, take} from 'rxjs';

export const initFiltersResolver: ResolveFn<void> = () => {
  const activatedRoute: ActivatedRoute = inject(ActivatedRoute);
  const loadUserDetailsCommandPort: LoadUserDetailsCommandPort = inject(
    LOAD_USER_DETAILS_COMMAND_PORT
  );

  return activatedRoute.params.pipe(
    take(1),
    switchMap((params: Params) =>
      loadUserDetailsCommandPort.loadUserDetails(params['userId'])
    )
  );
};
