import {
  ChangeDetectionStrategy,
  Component,
  Inject,
  OnDestroy,
  OnInit,
} from '@angular/core';
import {
  BehaviorSubject,
  combineLatestWith,
  Observable,
  Subject,
  switchMap,
  takeUntil,
} from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import {
  GET_ALL_USERS_QUERY_PORT,
  GetAllUsersQueryPort,
} from '../../../../application/ports/primary/query/get-all-users.query-port';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormControl, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { map } from 'rxjs/operators';

@Component({
  selector: 'lib-users-list',
  styleUrls: ['./users-list.component.scss'],
  templateUrl: './users-list.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, RouterModule, ReactiveFormsModule],
})
export class UsersListComponent implements OnInit, OnDestroy {
  readonly searchForm: FormGroup = new FormGroup({ search: new FormControl() });
  private readonly _onDestroy$ = new Subject<void>();
  private _searchSubject: BehaviorSubject<string> = new BehaviorSubject<string>(
    ''
  );
  private _sortByNameAscSubject: BehaviorSubject<boolean> =
    new BehaviorSubject<boolean>(true);

  public search$: Observable<string> = this._searchSubject.asObservable();
  public sortByNameAsc$: Observable<boolean> =
    this._sortByNameAscSubject.asObservable();

  readonly users$: Observable<UserQuery[]> = this._getAllUsersQueryPort
    .getAllUsers()
    .pipe(
      combineLatestWith(this.search$),
      map(([users, search]: [UserQuery[], string]) =>
        search
          ? users.filter(
              (user: UserQuery) =>
                user.username.toLowerCase().includes(search.toLowerCase()) ||
                user.name.toLowerCase().includes(search.toLowerCase())
            )
          : users
      ),
      switchMap((users) => this.sortByName(users))
    );

  sortByName(users: UserQuery[]) {
    return this._sortByNameAscSubject.asObservable().pipe(
      map((sort) => {
        if (sort) {
          return users.sort((a, b) => a.name.localeCompare(b.name));
        }
        return users.sort((a, b) => b.name.localeCompare(a.name));
      })
    );
  }

  constructor(
    @Inject(GET_ALL_USERS_QUERY_PORT)
    private _getAllUsersQueryPort: GetAllUsersQueryPort
  ) {}

  ngOnInit() {
    this.searchForm
      .get('search')
      ?.valueChanges.pipe(
        map((search: string) => this._searchSubject.next(search)),
        takeUntil(this._onDestroy$)
      )
      .subscribe();
  }

  ngOnDestroy(): void {
    this._onDestroy$.next();
    this._onDestroy$.complete();
  }

  changeSort() {
    this._sortByNameAscSubject.next(!this._sortByNameAscSubject.value);
  }
}
<div class="container mt-5 w-25 justify-content-center">
  <form [formGroup]="searchForm">
    <input
      type="text"
      formControlName="search"
      placeholder="Search"
      class="form-control"
    />
  </form>
</div>

<div class="container mt-5">
  <ng-container *ngTemplateOutlet="sortByName"></ng-container>
  <div class="row mt-3">
    <div class="col-4" *ngFor="let user of users$ | async">
      <div class="card mb-3 cursor-pointer" [routerLink]="'/users/' + user.id">
        <img [src]="user.avatar" alt="Avatar" class="img-fluid"/>
        <div class="pt-2 ps-1">
          <h5 class="card-title">{{ user.name }}</h5>
          <p class="card-text">{{ user.username }}</p>
        </div>
      </div>
    </div>
  </div>
</div>


<ng-template #sortByName>
  <div class="d-flex justify-content-end">

    <!--    <div class="dropdown">-->
    <!--      <button class="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown">-->
    <!--        Sort by name-->
    <!--      </button>-->
    <!--      <ul class="dropdown-menu">-->

    <!--        <li><a class="dropdown-item">Asc</a></li>-->
    <!--        <li><a class="dropdown-item">Desc</a></li>-->
    <!--      </ul>-->
    <!--    </div>-->
    <button class="btn btn-primary" *ngIf="(sortByNameAsc$ | async)" (click)="changeSort()">Sort by name ASC</button>
    <button class="btn btn-primary" *ngIf="!(sortByNameAsc$ | async)" (click)="changeSort()">Sort by name DESC</button>
  </div>
</ng-template>
