import {
  ChangeDetectionStrategy,
  Component,
  Inject,
  OnDestroy,
  OnInit,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormControl, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import {BehaviorSubject, combineLatestWith, Observable, Subject, takeUntil} from 'rxjs';
import { map, switchMap } from 'rxjs/operators';
import { PhotoQuery } from '../../../../application/ports/primary/query/photo.query';
import {
  GET_PHOTOS_FOR_SPECIFIC_USER_QUERY_PORT,
  GetPhotosForSpecificUserQueryPort,
} from '../../../../application/ports/primary/query/get-photos-for-specific-user.query-port';

@Component({
  selector: 'lib-photos',
  styleUrls: ['./photos.component.scss'],
  templateUrl: './photos.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
})
export class PhotosComponent implements OnInit, OnDestroy {
  private readonly _onDestroy$ = new Subject<void>();

  private _priceFilterSubject: BehaviorSubject<number> =
    new BehaviorSubject<number>(50);

  public priceFilter$: Observable<number> =
    this._priceFilterSubject.asObservable();

  readonly photos$: Observable<PhotoQuery[]> = this._activatedRoute.params.pipe(
    switchMap((params: Params) =>
      this._getPhotosForSpecificUserQueryPort.getPhotosForSpecificUser(
        params['userId']
      )
    ),
    combineLatestWith(this.priceFilter$),
    map(([photos, price])=> photos.filter((photo)=> photo.price < price))
  );

  readonly filterByPriceForm: FormGroup = new FormGroup({
    price: new FormControl(),
  });

  constructor(
    @Inject(GET_PHOTOS_FOR_SPECIFIC_USER_QUERY_PORT)
    private _getPhotosForSpecificUserQueryPort: GetPhotosForSpecificUserQueryPort,
    private _activatedRoute: ActivatedRoute
  ) {}

  ngOnInit() {
    this.filterByPriceForm
      .get('price')
      ?.valueChanges.pipe(
        map((price) => this._priceFilterSubject.next(price)),
        takeUntil(this._onDestroy$)
      )
      .subscribe();
  }

  ngOnDestroy(): void {
    this._onDestroy$.next();
    this._onDestroy$.complete();
  }
}
<form [formGroup]="filterByPriceForm" class="w-25">
  <input type="range" class="form-range" min="0" max="100" step="5" value="50" formControlName="price"
         placeholder="Price"/> {{priceFilter$ | async | currency}}
</form>

<div class="container mt-5">
  <div class="row">
    <div class="col-4" *ngFor="let photo of photos$ | async">
      <div class="card mb-3">
        <img [src]="photo.thumbnailUrl" class="card-img-top"/>
        <div class="card-body">
          <h5 class="card-title">{{ photo.title }}</h5>
          <p class="card-text">Author: {{ photo.author }}</p>
          <p class="card-text">Price: {{ photo.price | currency }}</p>
        </div>
      </div>
    </div>
  </div>
</div>
