import { ChangeDetectionStrategy, Component, Inject, ViewEncapsulation } from '@angular/core';
import { Observable } from 'rxjs';
import { PhotoQuery } from '../../../../application/ports/primary/query/photo.query';
import { GET_ALL_PHOTOS_QUERY_PORT, GetAllPhotosQueryPort } from '../../../../application/ports/primary/query/get-all-photos.query-port';

@Component({
  selector: 'lib-album',
  styleUrls: ['./album.component.scss'],
  templateUrl: './album.component.html',
  encapsulation: ViewEncapsulation.Emulated,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class AlbumComponent {
  readonly photos$: Observable<PhotoQuery[]> = this._getAllPhotosQueryPort.getAllPhotos();

  constructor(@Inject(GET_ALL_PHOTOS_QUERY_PORT) private _getAllPhotosQueryPort: GetAllPhotosQueryPort) {
  }
}
<div class="container">
  <div class="row">
    <ng-container *ngFor="let photo of photos$ | async">
      <div class="col-md-4 mb-4">
        <div class="card">
          <img [src]="photo.thumbnailUrl" class="card-img-top">
          <div class="card-body">
            <h5 class="card-title">{{ photo.title }}</h5>
            <p class="card-text">Author: {{ photo.author }}</p>
            <p class="card-text">Price: {{ photo.price }}</p>
          </div>
        </div>
      </div>
    </ng-container>
  </div>
</div>
