import { ChangeDetectionStrategy, Component, Inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Observable } from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import { GET_USER_DETAILS_QUERY_PORT, GetUserDetailsQueryPort } from '../../../../application/ports/primary/query/get-user-details.query-port';
import { BsModalRef } from 'ngx-bootstrap/modal';

@Component({
  selector: 'lib-edit-user-details-modal',
  styleUrls: ['./edit-user-details-modal.component.scss'],
  templateUrl: './edit-user-details-modal.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule],
})
export class EditUserDetailsModalComponent implements OnInit {
  title?: string;
  closeBtnName?: string;
  readonly userDetails$: Observable<UserQuery> = this._getUserDetailsQueryPort.getUserDetails();

  constructor(public bsModalRef: BsModalRef, @Inject(GET_USER_DETAILS_QUERY_PORT) private _getUserDetailsQueryPort: GetUserDetailsQueryPort) { }

  ngOnInit() { }
}
