import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormControl, FormGroup } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import { Observable } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { PhotoQuery } from '../../../../application/ports/primary/query/photo.query';
import { GET_PHOTOS_FOR_SPECIFIC_USER_QUERY_PORT, GetPhotosForSpecificUserQueryPort } from '../../../../application/ports/primary/query/get-photos-for-specific-user.query-port';

@Component({
  selector: 'lib-photos',
  styleUrls: ['./photos.component.scss'],
  templateUrl: './photos.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule],
})
export class PhotosComponent {
  readonly photos$: Observable<PhotoQuery[]> = this._activatedRoute.params.pipe(
    switchMap((params: Params) =>
      this._getPhotosForSpecificUserQueryPort.getPhotosForSpecificUser(
        params['userId']
      )
    )
  );
  readonly filterByPrice: FormGroup = new FormGroup({ price: new FormControl() });

  constructor(
    @Inject(GET_PHOTOS_FOR_SPECIFIC_USER_QUERY_PORT)
    private _getPhotosForSpecificUserQueryPort: GetPhotosForSpecificUserQueryPort,
    private _activatedRoute: ActivatedRoute
  ) { }
}
<div class="container mt-5">
  <div class="row">
    <div class="col-4" *ngFor="let photo of photos$ | async">
      <div class="card mb-3">
        <img [src]="photo.thumbnailUrl" class="card-img-top">
        <div class="card-body">
          <h5 class="card-title">{{ photo.title }}</h5>
          <p class="card-text">Author: {{ photo.author }}</p>
          <p class="card-text">Price: {{ photo.price | currency}}</p>
        </div>
      </div>
    </div>
  </div>
</div>
