import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import {
  GET_ALL_USERS_QUERY_PORT,
  GetAllUsersQueryPort,
} from '../../../../application/ports/primary/query/get-all-users.query-port';
import { CommonModule } from '@angular/common';
import { MatListModule } from '@angular/material/list';
import { RouterModule } from '@angular/router';
import { MatLine, MatLineModule } from '@angular/material/core';

@Component({
  selector: 'lib-users-list',
  styleUrls: ['./users-list.component.scss'],
  templateUrl: './users-list.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, MatListModule, RouterModule, MatLineModule],
})
export class UsersListComponent {
  readonly users$: Observable<UserQuery[]> = this._getAllUsersQueryPort
    .getAllUsers()
    .pipe(
      tap((x) => console.log(x)),
      tap((x) => console.log('asda'))
    );

  constructor(
    @Inject(GET_ALL_USERS_QUERY_PORT)
    private _getAllUsersQueryPort: GetAllUsersQueryPort
  ) {}
}
<table mat-table [dataSource]="users$" class="mat-elevation-z8">
  <!-- Kolumna z imieniem -->
  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> Imię </th>
    <td mat-cell *matCellDef="let user"> {{ user.name }} </td>
  </ng-container>

  <!-- Kolumna z nazwiskiem -->
  <ng-container matColumnDef="username">
    <th mat-header-cell *matHeaderCellDef> Nazwisko </th>
    <td mat-cell *matCellDef="let user"> {{ user.username }} </td>
  </ng-container>

  <!-- Dodatkowe kolumny (np. szczegóły użytkownika) można dodać tutaj -->

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
</table>
