import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { Observable } from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import {
  GET_USER_DETAILS_QUERY_PORT,
  GetUserDetailsQueryPort,
} from '../../../../application/ports/primary/query/get-user-details.query-port';
import { NavbarComponent } from '@core';
import { EditDetailsDirective } from '../../directives/edit-details/edit-details.directive';

@Component({
  selector: 'lib-user-details',
  templateUrl: './user-details.component.html',
  styleUrls: ['./user-details.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, RouterModule, NavbarComponent, EditDetailsDirective],
})
export class UserDetailsComponent {
  readonly userDetails$: Observable<UserQuery> =
    this._getUserDetailsQueryPort.getUserDetails();

  constructor(
    @Inject(GET_USER_DETAILS_QUERY_PORT)
    private _getUserDetailsQueryPort: GetUserDetailsQueryPort
  ) {}
}
<lib-navbar [showSearch]="false"></lib-navbar>

<ng-container *ngIf="userDetails$ | async as user">
  <div class="container">
    <div class="row justify-content-center mt-5">
      <div class="col-md-6">
        <div class="card">
          <img [src]="user.avatar" class="card-img-top"/>
          <div class="card-body">
            <h5 libEditDetails="name" class="card-title  cursor-pointer">{{ user.name }}</h5>
            <p libEditDetails="username" class="card-text cursor-pointer">
              {{ user.username }}
            </p>
            <p libEditDetails="email" class="card-text cursor-pointer">
              Email: {{ user.email }}
            </p>
            <p libEditDetails="phone" class="card-text cursor-pointer">
              Phone: {{ user.phone }}
            </p>
            <p libEditDetails="website" class="card-text cursor-pointer">
              Website: {{ user.website }}
            </p>
            <div class="justify-content-between d-flex">
              <a href="mailto:{{ user.email }}" class="btn btn-primary"
              >Send Email</a
              >
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</ng-container>
