import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormControl, FormGroup } from '@angular/forms';
import { BehaviorSubject, Observable } from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import { GET_USER_DETAILS_QUERY_PORT, GetUserDetailsQueryPort } from '../../../../application/ports/primary/query/get-user-details.query-port';

@Component({
  selector: 'lib-user-details',
  styleUrls: ['./user-details.component.scss'],
  templateUrl: './user-details.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, RouterModule],
})
export class UserDetailsComponent {
  readonly userDetails$: Observable<UserQuery> =
    this._getUserDetailsQueryPort.getUserDetails();

  private _searchSubject: BehaviorSubject<string> = new BehaviorSubject<string>(
    ''
  );
  public search$: Observable<string> = this._searchSubject.asObservable();
  readonly searchForm: FormGroup = new FormGroup({ search: new FormControl() });

  constructor(
    @Inject(GET_USER_DETAILS_QUERY_PORT)
    private _getUserDetailsQueryPort: GetUserDetailsQueryPort
  ) { }

  onSearchFormSubmitted(searchForm: FormGroup): void {
  }
}
<ng-container *ngIf="userDetails$ | async as user">
  <div class="container">
    <div class="row justify-content-center mt-5">
      <div class="col-md-6">
        <div class="card">
          <img [src]="user.avatar" alt="Avatar" class="card-img-top"/>
          <div class="card-body">
            <h5 class="card-title">{{ user.name }}</h5>
            <p class="card-text">{{ user.username }}</p>
            <p class="card-text">Email: {{ user.email }}</p>
            <p class="card-text">Address: {{ user.address }}</p>
            <p class="card-text">Phone: {{ user.phone }}</p>
            <p class="card-text">Website: {{ user.website }}</p>
            <p class="card-text">Company: {{ user.company }}</p>
            <div class="justify-content-between d-flex">
              <a href="mailto:{{ user.email }}" class="btn btn-primary"
              >Send Email</a
              >
              <a href="{{ user.website }}" class="btn btn-primary"
              >Go to website</a
              >
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</ng-container>
<form [formGroup]="searchForm" (ngSubmit)="onSearchFormSubmitted(searchForm)">
  <input type="text" formControlName="search" placeholder="Search"/><input
  type="submit"
  value="Submit"
/>
</form>
