import {
  ChangeDetectionStrategy,
  Component,
  EventEmitter,
  Input,
  OnDestroy,
  OnInit,
  Output,
} from '@angular/core';
import { FormControl, FormGroup, ReactiveFormsModule } from '@angular/forms';
import { CommonModule, Location } from '@angular/common';
import { BehaviorSubject, Subject, takeUntil } from 'rxjs';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'lib-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule, RouterModule],
})
export class NavbarComponent implements OnInit, OnDestroy {
  @Input() showSearch: boolean = false;
  @Input() showBack: boolean = true;
  @Input() count: number = 0;
  @Input() summaryTitle: string = '';
  @Output() searchValueEvent: EventEmitter<string> = new EventEmitter<string>();

  private readonly _onDestroy$ = new Subject<void>();
  private _searchSubject: BehaviorSubject<string> = new BehaviorSubject<string>(
    ''
  );

  readonly search$ = this._searchSubject.asObservable();

  readonly searchForm: FormGroup = new FormGroup({ search: new FormControl() });

  constructor(private _location: Location) {}

  ngOnInit(): void {
    this.searchForm
      .get('search')
      ?.valueChanges.pipe(takeUntil(this._onDestroy$))
      .subscribe((search: string) => {
        this._searchSubject.next(search);
        this.searchValueEvent.emit(search);
      });
  }

  ngOnDestroy(): void {
    this._onDestroy$.next();
    this._onDestroy$.complete();
  }

  goBack(): void {
    this._location.back();
  }

  resetSearch() {
    this.searchForm.patchValue({ search: '' });
  }
}
<nav class="navbar bg-dark">
  <div class="d-flex w-100 align-items-center">
    <div class="d-flex justify-content-start w-75 ms-4 my-3">
      <i
        class="bi bi-caret-left-fill text-white d-flex align-items-center me-3 size-30-px cursor-pointer "
        *ngIf="showBack"
        (click)="goBack()"
      ></i>
      <a
        class="navbar-brand btn btn-primary text-white d-flex align-items-center justify-content-center w-25 h-75"
        [routerLink]="'/users'"
      >Users</a
      >
      <a
        class="navbar-brand btn btn-primary text-white d-flex align-items-center justify-content-center w-25 h-75"
        [routerLink]="'/album'"
      >Photos</a
      >
    </div>
    <div class="me-5 w-25">
      <form *ngIf="showSearch" [formGroup]="searchForm">
        <input
          type="text"
          formControlName="search"
          placeholder="Search"
          class="form-control mb-2"
        />
        <button class="btn btn-outline-secondary text-white me-2" *ngIf="(search$ | async)?.length"
                (click)="resetSearch()">
          <i class="bi bi-x-lg"></i>
        </button>
        <span class="text-white">{{ summaryTitle }}: {{ count }}</span>
      </form>
    </div>
  </div>
</nav>
