import { ChangeDetectionStrategy, Component, Inject } from '@angular/core';
import { Observable } from 'rxjs';
import { UserQuery } from '../../../../application/ports/primary/query/user.query';
import {
  GET_ALL_USERS_QUERY_PORT,
  GetAllUsersQueryPort,
} from '../../../../application/ports/primary/query/get-all-users.query-port';
import { CommonModule } from '@angular/common';
import { MatListModule } from '@angular/material/list';
import { RouterModule } from '@angular/router';
import { MatGridListModule } from '@angular/material/grid-list';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'lib-users-list',
  styleUrls: ['./users-list.component.scss'],
  templateUrl: './users-list.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [
    CommonModule,
    MatListModule,
    RouterModule,
    MatGridListModule,
    MatCardModule,
    MatButtonModule,
  ],
})
export class UsersListComponent {
  readonly users$: Observable<UserQuery[]> =
    this._getAllUsersQueryPort.getAllUsers();

  constructor(
    @Inject(GET_ALL_USERS_QUERY_PORT)
    private _getAllUsersQueryPort: GetAllUsersQueryPort
  ) {}
}
<mat-grid-list cols="3" rowHeight="300px" gutterSize="16px">
  <mat-grid-tile *ngFor="let user of users$ | async">
    <mat-card>
      <img [src]="user.avatar" alt="Avatar" class="user-avatar">
      <mat-card-header>
        <mat-card-title>{{ user.name }}</mat-card-title>
        <mat-card-subtitle>{{ user.username }}</mat-card-subtitle>
      </mat-card-header>
      <mat-card-content>
      </mat-card-content>
      <mat-card-actions>
        <button
          mat-raised-button
          color="primary"
          routerLink="/user/{{ user.id }}"
          class="mat-button-hovered"
        >
          Details
        </button>
      </mat-card-actions>
    </mat-card>
  </mat-grid-tile>
</mat-grid-list>
