import {Inject, Injectable} from '@angular/core';
import {Observable} from 'rxjs';
import {map, switchMap} from 'rxjs/operators';
import {GetAllUsersQueryPort} from '../ports/primary/query/get-all-users.query-port';
import {GetUserDetailsQueryPort} from '../ports/primary/query/get-user-details.query-port';
import {InitDetailsCommandPort} from '../ports/primary/command/init-details.command-port';
import {GET_ALL_USERS_DTO_PORT, GetAllUsersDtoPort,} from '../ports/secondary/dto/get-all-users.dto-port';
import {GET_ONE_USER_DTO_PORT, GetOneUserDtoPort,} from '../ports/secondary/dto/get-one-user.dto-port';
import {PATCH_USER_CONTEXT_PORT, PatchUserContextPort,} from '../ports/secondary/context/patch-user.context-port';
import {SELECT_USER_CONTEXT_PORT, SelectUserContextPort,} from '../ports/secondary/context/select-user.context-port';
import {GET_ONE_PHOTO_DTO_PORT, GetOnePhotoDtoPort,} from '../ports/secondary/dto/get-one-photo.dto-port';
import {UserQuery} from '../ports/primary/query/user.query';
import {UserDTO} from '../ports/secondary/dto/user.dto';

@Injectable()
export class UserAlbumsState
  implements
    GetAllUsersQueryPort,
    GetUserDetailsQueryPort,
    InitDetailsCommandPort
{
  constructor(
    @Inject(GET_ALL_USERS_DTO_PORT)
    private _getAllUsersDtoPort: GetAllUsersDtoPort,
    @Inject(GET_ONE_USER_DTO_PORT)
    private _getOneUserDtoPort: GetOneUserDtoPort,
    @Inject(PATCH_USER_CONTEXT_PORT)
    private _patchUserContextPort: PatchUserContextPort,
    @Inject(SELECT_USER_CONTEXT_PORT)
    private _selectUserContextPort: SelectUserContextPort,
    @Inject(GET_ONE_PHOTO_DTO_PORT)
    private _getOnePhotoDtoPort: GetOnePhotoDtoPort
  ) {}

  getAllUsers(): Observable<UserQuery[]> {
    return this._getAllUsersDtoPort
      .getAllUsers()
      .pipe(
        map((allUsers: UserDTO[]) =>
          allUsers.map((user) => this.mapToUserQuery(user))
        )
      );
  }

  getUserDetails(): Observable<UserQuery> {
    return this._selectUserContextPort.select$.pipe(
      map((user: UserDTO) => this.mapToUserQuery(user))
    );
  }

  initDetails(id: number): Observable<void> {
    return this._getOneUserDtoPort
      .getOneUser(id)
      .pipe(
        switchMap((user: UserDTO) => this._patchUserContextPort.patch(user))
      );
  }

  private mapToUserQuery(user: UserDTO) {
    return new UserQuery(
      user.id,
      this.getAvatar(),
      user.name,
      user.username,
      user.email,
      this.getAddress(user),
      user.phone,
      user.website,
      user.company.name
    );
  }

  private getAddress(user: UserDTO) {
    return `${user.address.street},${user.address.zipcode},${user.address.city},`;
  }

  private getAvatar() {
    return 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/cf/Angular_full_color_logo.svg/512px-Angular_full_color_logo.svg.png';
  }

  initPhotos(id: number): Observable<void> {
    return this._getOnePhotoDtoPort.getOnePhoto(id);
  }
}
