import { inject, InjectionToken } from '@angular/core';
import { ActivatedRoute, ActivatedRouteSnapshot } from '@angular/router';
import {from, Observable, of, tap} from 'rxjs';

export const USER_ID_PROVIDER: InjectionToken<UserIdProvider> =
  new InjectionToken<UserIdProvider>('USER_ID_PROVIDER', {
    factory: (): UserIdProvider => {
      const route: ActivatedRoute = inject(ActivatedRoute);

      return {
        userId$: of(route.snapshot.params['userId']).pipe(tap((x)=> console.log(x))),
      };
    },
  });

export interface UserIdProvider {
  userId$: Observable<string>;
}
